package module_1;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//primitive Data types: int, double, char, boolean.
//1.int
		int a = 10;
		int b = -10;
		int c = 0;
		int x, y, z;

		// 2. double

		double d1 = 10.05;
		double d2 = -10.52;
		double d3 = 11.23;
		double d4 = 100;// 100.00

		// 3. char: only single digit value: must be in ''.
		char c1 = 'a';
		char c2 = 'A';
		char c3 = '1';
		char c4 = '$';
//char c5='aa'; not allowed.

		// 4. boolean
		boolean b1 = true;
		boolean b2 = false;

		// 5. Sting: it is not a data type it is class. but we can use as type.

		String s1 = "Rama";
		String s2 = "This my java code";
		String s3 = "1000";
		String s4 = "15.234";

	}

}
