package module_1;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// simple Loop:
		for (int i = 0; i <= 10; i++) {
			System.out.println(i);
		}

		// while loop
		int y = 0;//Initialization
		while (y <= 10)//Condtional 
		{
			System.out.print(y);
			y++;//increment or decrement 
		}
		System.out.println("");
	for (int z = 10; z >=1; z--) {
		System.out.print(z);
	}
	}

}
